import "./App.css";
import Home from "./components/Home";
import Search from "./components/Search";
import { useState } from "react";
import { getAll, update } from "./BooksAPI";
import { useEffect } from "react";



function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);
  const [refreshShelf, setRefreshShelf] = useState(true);
  const [books, setBooks] = useState([]);
  console.log(books);

  const listenToChanges = (book) =>{
    (async function (){
      const respond = await update(book.bo,shelf)
      console.log(respond)
    })()
    setRefreshShelf(true)
  }

  const syncData = async () => {
    const data = await getAll();
    setBooks(data);
  };

  useEffect(() => {
    if (refreshShelf) {
     
      syncData();
      setRefreshShelf(false);
    }
  });
  return (
    <div className="app">
      {showSearchPage ? (
        <Search setShowSearchpage={setShowSearchpage} showSearchPage={showSearchPage}></Search>
      ) : (
        <Home books={books} listen={listenToChanges} setShowSearchpage={setShowSearchpage} showSearchPage={showSearchPage}></Home>
      )}
    </div>
  );
}

export default App;
