import { useState } from "react";
import { search } from "../BooksAPI";
import Book from "./Book";
import SearchBar from "./SearchBar"
const Search = (props) => {
    
    const [results , setResults] = useState([]);
    const getbooks = (books) =>{
        setResults(books)
    }

    useEffect(()=>{
        
      const doIt = async() =>{
          result = await searchFn(search)
          if(result){
          onGetBooks(result)
          }
          onGetBooks([])
      } 
      
     doIt() 
  
  },[])
  
  return (
    <div className="search-books">
      <div className="search-books-bar">
        <a
          className="close-search"
          onClick={() => props.setShowSearchpage(!props.showSearchPage)}
        >
          Close
        </a>
        <SearchBar searchFn={search} onGetBooks={getbooks}></SearchBar>
      </div>
      <div className="search-books-results">
        <ol className="books-grid">
            {results.length > 0 ? 
                results.map(book=><Book key={book.id} ins={book} listen ={props.listen}></Book>) : <div>empty</div>}
        </ol>
      </div>
    </div>
  );
};

export default Search;
