import { useState } from "react";
import { useEffect } from "react";


const SearchBar = ({onGetBooks, searchFn}) => {
    const [search, setSearch] = useState("")

    const handleSearch = (event) =>{
        setSearch(event.target.value)
    }

    useEffect(()=>{
        
        const doIt = async() =>{
            const result = await searchFn(search)
            if(result){
            onGetBooks(result)
            }
            onGetBooks([])
        } 
        
       doIt() 
    
    },[search, searchFn])
    
  return (
    <div className="search-books-input-wrapper">
      <input value={search} onChange={handleSearch} type="text" placeholder="Search by title, author, or ISBN" />
    </div>
  );
};

export default SearchBar;
