import React from "react";
import { useEffect } from "react";
import { update } from "../BooksAPI";

const BookForm = ({ listen, book }) => {
  const [bookState, setBookState] = React.useState("")
  let bookStateChange = false
 

  const bookStateHandler = (event) => {
    console.log(event.target.value);
    setBookState(event.target.value);
    bookStateChange = true
    
  
  };
  useEffect(() => {
    if(bookStateChange){
    console.log("change");
    const change = async () => {
      try {
        const report = await update(book, bookState);
        listen()
        console.log(report);
      } catch (e) {
        console.log("error in changing shelf", e);
      }
    };
    change();
    bookStateChange = false
  }
    
  }, [b]);
  

  return (
    <div className="book-shelf-changer">
      <select onChange={bookStateHandler} value={bookState}>
        <option value="none" disabled>
          Move to...
        </option>
        <option value="currentlyReading">Currently Reading</option>
        <option value="wantToRead">Want to Read</option>
        <option value="read">Read</option>
        <option value="none">None</option>
      </select>
    </div>
  );
};

export default BookForm;
