import "./App.css";
import BookShelf from "./components/BookShelf";
import Search from "./components/Search";
import { useState } from "react";
import { getAll, update } from "./BooksAPI";
import { useEffect } from "react";
import { Routes, Route } from "react-router-dom";

function App() {
  const [refreshShelf, setRefreshShelf] = useState(true);
  const [books, setBooks] = useState([]);

  console.log(books);

  const listenToChanges = () => {
    setRefreshShelf(true);
  };

  const syncData = async () => {
    const data = await getAll();
    setBooks(data);
  };

  useEffect(() => {
    if (refreshShelf) {
      syncData();
      setRefreshShelf(false);
    }
  }, [refreshShelf]);
  return (
    <div className="app">
      <Routes>
        <Route
          path="/"
          element={<Home books={books} listen={listenToChanges}></Home>}
        />
        <Route
          path="/search"
          element={<Search listen={listenToChanges}></Search>}
        />
      </Routes>
    </div>
  );
}

export default App;
