import React from 'react'
import { useEffect } from 'react';
import { update } from '../BooksAPI';

const BookForm = ({listen, book,listBooks}) => {
    const [bookState, setBookState] = React.useState(book.shelf)
    const bookStateHandler = (event) =>{
        console.log(event.target.value)
        setBookState(event.target.value)
        listen()
        
    }
    useEffect(()=>{
      console.log("change")
      const change = async()=>{
      const report = await update(book,bookState)
      console.log(report)
      }
      change()
      
    },[bookState])
    
    
  return (
    <div className="book-shelf-changer">
      <select onChange={bookStateHandler} value={bookState}>
        <option value="none" disabled>
          Move to...
        </option>
        <option value="currentlyReading">Currently Reading</option>
        <option value="wantToRead">Want to Read</option>
        <option value="read">Read</option>
        <option value="none">None</option>
      </select>
    </div>
  );
};

export default BookForm;
