import BookForm from "./BookForm";

const Book = ({listen, ins}) => {
  return (
    <li>
      <div className="book">
        <div className="book-top">
          <div
            className="book-cover"
            style={{
              width: 128,
              height: 193,
              backgroundImage:
                `url(${ins.imageLinks.thumbnail})`,
            }}
          ></div>
          <BookForm book={ins} listen={listen}></BookForm>
        </div>
        <div className="book-title">{ins.title}</div>
        <div className="book-authors">{ins.authors.map(x => "||\n"+x)}</div>
      </div>
    </li>
  );
};

export default Book;
