import "./App.css";
import Home from "./components/Home";
import Search from "./components/Search";
import { useState } from "react";
import { getAll, update } from "./BooksAPI";
import { useEffect } from "react";
import {Routes, Route} from "react-router-dom"




function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);
  const [refreshShelf, setRefreshShelf] = useState(true);
  const [books, setBooks] = useState([]);
  
  console.log(books);

  const  listenToChanges =() =>{
    setRefreshShelf(true)
    
  }

  const syncData = async () => {
    const data = await getAll();
    setBooks(data);
  };

  useEffect(() => {
    if (refreshShelf) {
     
      syncData();
      setRefreshShelf(false);
    }
  },[refreshShelf]);
  return (
    <div className="app">
      <Routes>

      
        <Search setShowSearchpage={setShowSearchpage} showSearchPage={showSearchPage} listen={listenToChanges} ></Search>
      
        <Home books={books} listen={listenToChanges} setShowSearchpage={setShowSearchpage} showSearchPage={showSearchPage}></Home>
      
      </Routes>
    </div>
  );
}

export default App;
