import { useState, useEffect } from "react";
import { search } from "../BooksAPI";
import Book from "./Book";
import SearchBar from "./SearchBar"
import { Link } from "react-router-dom";

const Search = (props) => {
    
    const [results , setResults] = useState([]);
    const [sta,setSta]= useState("")
   
    console.log("search", props.books)
    const getbooks = (books) =>{
        setSta(books)
        
    }

    
    useEffect(()=>{
        
      
      console.log("search")

      const doIt = async() =>{
        try{
          if(sta === ""){
            setResults([])
          }
          console.log(sta)
          const response = await search(sta)
          if(response !== undefined){
            console.log(response)
            setResults(response)

          }else{
            throw Error
          }
        
          }catch (e){
            console.log(e)
          }
          
        }
      
     doIt() 
      
  
  },[sta])

 
  
  return (
    <div className="search-books">
      <div className="search-books-bar">
        <Link to={"/"}
          className="close-search"
        >
          Close
        </Link>
        <SearchBar onGetBooks={getbooks}></SearchBar>
      </div>
      <div className="search-books-results">
        <ol className="books-grid">
            {typeof results === typeof [] && results.length > 0 ? 
                results.map(book=><Book key={book.id} ins={book} listen ={props.listen} listBooks={props.books}></Book>) : <div>empty</div>}
        </ol>
      </div>
    </div>
  );
};

export default Search;
