import { useState } from "react";
import { useEffect } from "react";


const SearchBar = ({onGetBooks, searchFn}) => {
    const [search, setSearch] = useState('alice')

    const handleSearch = (event) =>{
        setSearch(event.target.value)
    }

    useEffect(()=>{
        console.log(search)
        searchFn(search).then(res=>console.log(res))
    },[search])
    
  return (
    <div className="search-books-input-wrapper">
      <input value={search} onChange={handleSearch} type="text" placeholder="Search by title, author, or ISBN" />
    </div>
  );
};

export default SearchBar;
