import { Routes, Route } from "react-router-dom";

function App() {
  
  const [books, setBooks] = useState([]);

  const syncData = async () => {
    const data = await getAll();
    setBooks(data);
  };


  return (
    <div className="app">
      <Routes>
        <Route
          path="/"
          element={<BookShelf books={books} listen={listenToChanges}></BookShelf>}
        />
        <Route
          path="/search"
          element={<Search listen={listenToChanges}></Search>}
        />
      </Routes>
    </div>
  );
}

export default App;
